mapper.select:
select
,
from COURT_CASE where del_flag=0 limit #{begin},#{size}

select * from COURT_CASE where del_flag=0 order by
UPDATE_TIME DESC
limit #{begin},#{size}

select * from COURT_CASE where del_flag=0 and ID in

	#{item}

order by UPDATE_TIME limit #{begin},#{size}

select case.*,court.org_name as courtName from COURT_CASE case, TP_ORG court

	case.DEL_FLAG =0
	
		and case.COURT_ID in
		
			#{item}
		
	
	 and court.id = case.court_id
	
		and case.case_num like '%'||#{caseNum}||'%'
	
	
		and CASE_TYPE_NAME = #{caseType}
	
	
		and case.REGISTER_TIME between #{startTime} and #{endTime}
	
	order by REGISTER_TIME DESC limit
	#{begin},#{size}

select count(*) from
COURT_CASE

 DEL_FLAG =0
	
	
	and COURT_ID in
	
		#{item}
	
	
	
	
		and case_num like '%'||#{caseNum}||'%'
	
	
		and CASE_TYPE_NAME = #{caseType}
	
	
		and REGISTER_TIME between #{startTime} and #{endTime}

SELECT case.*,court.org_name as courtName FROM COURT_CASE case,TP_ORG court

	case.COURT_ID in
	
		#{item}
	
	and case.DEL_FLAG =0 and case.court_id = court.id

order by case.REGISTER_TIME DESC limit #{begin},#{size}

select

	distinct

c.*
from COURT_CASE c

	left join COURT_CASE_PLAN p on c.id = p.case_id


	left join TP_ORG o on c.court_id=o.id

where
1=1

	and c.case_type_name = #{caseType}


 and p.plan_start_time >= #{startTime} 


 and p.plan_end_time <= #{endTime} 


	and c.court_id=#{courtId}


	and o.org_name like #{courtName}


	and c.case_name like #{caseName}


	order by ${orderByClause}


	limit #{startIndex},#{pageSize}
