SELECT court.org_name as courtName,case.case_num as caseNum,case.case_name as caseName,plan.* FROM 
COURT_CASE_PLAN plan,COURT_CASE case,Tp_Org court

	plan.DEL_FLAG=0 and case.DEL_FLAG=0 and court.id = plan.court_id and plan.case_id = case.id
	
	and plan.COURT_ID in
	
		#{item}
	
	
	
		and plan.PLAN_START_TIME > #{BillboardTime}
	
	
		and plan.PLAN_START_TIME <  #{pubTime} and plan.PLAN_END_TIME > #{pubTime}
	
	
		and plan.PLAN_END_TIME <  #{vodTime}
	

order by plan.PLAN_START_TIME  DESC
limit #{begin},#{size}

SELECT court.org_name as courtName,case.case_num as caseNum,case.case_name as caseName,plan.* FROM 
COURT_CASE_PLAN plan,COURT_CASE case,Tp_Org court

	plan.DEL_FLAG=0 and case.DEL_FLAG=0 and court.id = plan.court_id and plan.case_id = case.id
	
	and plan.COURT_ID in
	
		#{item}
	
	
	
		and plan.PLAN_START_TIME > #{BillboardTime}
	
	
		and plan.PLAN_START_TIME <  #{pubTime} and plan.PLAN_END_TIME > #{pubTime}
	
	
		and plan.PLAN_END_TIME <  #{vodTime}
	
	
		and plan.PLAN_START_TIME between  #{startTime} and #{endTime}
	
	
		and case.case_num like  '%'||#{caseLikeName}||'%'
	

order by plan.PLAN_START_TIME  DESC
limit #{begin},#{size}

SELECT c.case_num,c.case_name,c.court_id,c.case_type_name,p.* FROM COURT_CASE_PLAN p left join COURT_CASE  c on c.id=p.CASE_ID

	p.DEL_FLAG=0 and c.DEL_FLAG=0
	
	and c.COURT_ID in
	
		#{item}
	
	
	
		and p.PLAN_START_TIME > #{BillboardTime}
	
	
		and p.PLAN_START_TIME <  #{pubTime} and p.PLAN_END_TIME > #{pubTime}
	
	
		and p.PLAN_END_TIME <  #{vodTime}
	

order by p.PLAN_START_TIME  DESC
limit #{begin},#{size}

