  SELECT court.org_name as courtName,case.case_num as caseNum,case.case_name as caseName,case.case_type_name as caseType,plan.* FROM 
		COURT_CASE_PLAN plan,COURT_CASE case,Tp_Org court
		
			plan.DEL_FLAG=0  and court.id = plan.court_id and plan.case_id = case.id
			
				and plan.PLAN_START_TIME between  #{startTime} and #{endTime}
			
			
				and plan.PLAN_START_TIME between  #{stopTime} and #{beginTime} and plan.PLAN_END_TIME between  #{stopTime} and #{beginTime}
			
			
				and case.case_type_name like  '%'||#{caseType}||'%'
			
			
				and court.org_name like  '%'||#{courtName}||'%'
			
		
		order by plan.CREATE_TIME  DESC
		limit #{begin},#{size}

SELECT count(plan.id) FROM COURT_CASE_PLAN plan,COURT_CASE case,Tp_Org court
plan.DEL_FLAG=0 and court.id = plan.court_id and plan.case_id = case.id
and plan.PLAN_START_TIME between #{startTime} and #{endTime}
and plan.PLAN_START_TIME between #{stopTime} and #{beginTime} and plan.PLAN_END_TIME between #{stopTime} and #{beginTime}
and case.case_type_name like '%'||#{caseType}||'%'
and court.org_name like '%'||#{courtName}||'%'

SELECT court.org_name as courtName,case.case_num as caseNum,case.case_name as caseName,case.case_type_name as caseType,plan.* FROM 
COURT_CASE_PLAN plan,COURT_CASE case,Tp_Org court

	plan.DEL_FLAG=0 and case.DEL_FLAG=0 and court.id = plan.court_id and plan.case_id = case.id
	
		and court.org_name like  '%'||#{courtName}||'%'
	
	
		and case.case_name like  '%'||#{caseName}||'%'
	
	
		and case.case_type_name like  '%'||#{caseType}||'%'
	
	
		and plan.trialLevel = #{trialLevel}
	
	
		and plan.courtId = #{courtId}
	
	
		and plan.PLAN_START_TIME between  #{startTime} and #{endTime}
	

order by plan.PLAN_START_TIME  DESC
limit #{begin},#{size}

